import React, {useEffect, useState} from 'react'
import {Navbar, Table, Container, Row, Col, Button, ButtonGroup, From, Form} from "react-bootstrap";

import {useDispatch, useSelector} from "react-redux";

import { loadUsers } from "./redux/actions";

const initialState = {
    name: "",
    email: "",
    gender: ""
}

const Home = () => {
  const [state, setState] = useState(initialState);
  const dispatch = useDispatch();
  const {users} = useSelector(state => state.data);

  const {name, email, gender} = state;

  useEffect(() => {
    dispatch(loadUsers());
  }, [])

  const handleChange = () => {};

  const handleSubmit = () => {};
  return (
    <>
    <Navbar bg="primary" variant="dark" className="justify-content-center">
      <Navbar.Brand>Python Flask MongoDB React Redux CRUD Application</Navbar.Brand>
    </Navbar>
    <Container style={{ marginTop: "70px" }}>
      <Row>
        <Col md={4}>
          <Form onSubmit={handleSubmit}>
            <Form.Group>
              <Form.Label>Name</Form.Label>
              <Form.Control
                type="text"
                placeholder="Name"
                name="name"
                value={name}
                onChange={handleChange}
                />
            </Form.Group>
            <Form.Group>
              <Form.Label>Email</Form.Label>
              <Form.Control
                type="text"
                placeholder="Email"
                name="email"
                value={email}
                onChange={handleChange}
                />
            </Form.Group>
            <Form.Group>
              <Form.Label>Gender</Form.Label>
              <Form.Control
                type="text"
                placeholder="Gender"
                name="gender"
                value={gender}
                onChange={handleChange}
                />
            </Form.Group>
          </Form>
        </Col>
        <Col md={8}>
          <Table bordered hover>
            <thead>
              <tr>
                <th>No.</th>
                <th>Name</th>
                <th>Email</th>
                <th>Gender</th>
                <th>Action</th>
              </tr>
            </thead>
            {users && users.map((item, index) => (
              <tbody key={index}>
                <tr>
                  <td>{index+1}</td>
                  <td>{item.name}</td>
                  <td>{item.email}</td>
                  <td>{item.gender}</td>
                  <td>
                    <ButtonGroup>
                      <Button
                        style={{ marginRight: "5px" }}
                        variant="danger"
                      >
                        Delete
                      </Button>
                      <Button variant="secondary">Update</Button>
                    </ButtonGroup>
                  </td>
                </tr>
              </tbody>
            ))}
          </Table>
        </Col>
      </Row>
    </Container>
    </>
  );
};

export default Home;
